SET NAMES UTF8;
DROP DATABASE IF EXISTS badboy;
CREATE DATABASE badboy CHARSET=UTF8;
USE badboy;

-- 演出城市
CREATE TABLE cities(
    cid INT PRIMARY KEY AUTO_INCREMENT,
    city VARCHAR(200)
);
-- 1~11

-- 用户列表
CREATE TABLE users(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(200),
    upwd VARCHAR(200),
    email VARCHAR(200),
    phone VARCHAR(200),
    avatar VARCHAR(200),
    -- 真实姓名
    rname VARCHAR(200),
    gender INT,
    -- 关联城市表
    cid INT,
    -- 星座
    const VARCHAR(200),
    -- 血型
    blood VARCHAR(200)
);
-- 1~4

-- 演出风格
CREATE TABLE styles(
  stid INT PRIMARY KEY AUTO_INCREMENT,
  stname VARCHAR(200)
);
-- 1~12

-- 艺人
CREATE TABLE artists(
    arid INT PRIMARY KEY AUTO_INCREMENT,
    arname VARCHAR(16),
    arphoto VARCHAR(128),
    -- 关联演出风格
    stid INT,
    -- 音乐人介绍
    profile  VARCHAR(1024)
);
-- 1~20
-- 艺人相关图片
CREATE TABLE art_pics(
    pid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联艺人
    arid INT, 
    -- 图片路径
    pic VARCHAR(128)
);
-- 艺人作品
CREATE TABLE albums(
    -- 作品编号
    alid INT PRIMARY KEY AUTO_INCREMENT,  
    arid INT
);
-- 艺人的粉丝
CREATE TABLE fans(
    fid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联艺人
    arid INT, 
    -- 关联用户
    uid  INT 
);
-- 演出表
CREATE TABLE shows(
    shid INT PRIMARY KEY AUTO_INCREMENT,
    shtitle VARCHAR(200),
    -- 演出信息
    description TEXT
);
-- 1~20


-- 想看演出的人
CREATE TABLE wants(
    wid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联演出
    shid INT, 
    -- 关联用户
    uid  INT 
);
-- 艺人与演出关联表
CREATE TABLE arshows(
    asid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联艺人
    arid INT,
    -- 关联演出
    shid INT
);
-- 演出现场
CREATE TABLE venues(
    vid INT PRIMARY KEY AUTO_INCREMENT,
    vname VARCHAR(32),
    vphone VARCHAR(11),
    vaddress  VARCHAR(64),
    vpic VARCHAR(128),
    cid INT
);
