-- 场次
CREATE TABLE tours(
    tourid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联现场
    vid INT, 
    -- 关联演出
    shid INT, 
    -- 总票数
    count INT,
    -- 价格
    price DECIMAL,
    -- 时间
    time BIGINT
);

-- 订单
CREATE TABLE orders(
    oid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联用户
    uid INT, 
    time BIGINT
);
-- 购票明细
CREATE TABLE tickets(
    tkid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联场次
    tourid INT, 
    count INT,
    -- 关联订单
    oid INT
);
-- 购物车
CREATE TABLE cartitems(
    iid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联场次
    tourid INT, 
    count INT,
    -- 关联用户
    uid INT
);












